        public static string doc1 = "<!DOCTYPE html>\r\n<html lang=\"en\">\r\n\r\n<head>\r\n  <meta charset=\"UTF-8\">\r\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n  <title>Sample HTML Code - NewsLetter Form</title>\r\n  <style>\r\n    @import url(\"https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap\");\r\n\r\n    body {\r\n      display: flex;\r\n      justify-content: center;\r\n      padding: 3rem 0;\r\n      font-family: \"Poppins\", sans-serif;\r\n      font-size: 1rem;\r\n      color: white;\r\n      background-color: #ff7a7a;\r\n    }\r\n\r\n    main {\r\n      max-width: 350px;\r\n      display: flex;\r\n      flex-direction: column;\r\n      align-items: center;\r\n    }\r\n\r\n    .intro {\r\n      display: flex;\r\n      flex-direction: column;\r\n      justify-content: center;\r\n      align-items: center;\r\n      text-align: center;\r\n      width: 100%;\r\n      margin-bottom: 3rem;\r\n    }\r\n\r\n    .title {\r\n      padding: 1rem;\r\n      font-size: 1.75rem;\r\n    }\r\n\r\n    .sign-up {\r\n      width: 100%;\r\n    }\r\n\r\n    .sign-up-para {\r\n      padding: 1rem 5rem;\r\n      margin-bottom: 1.75rem;\r\n      border-radius: 0.8rem;\r\n      box-shadow: 0 8px 0px rgba(0 0 0/0.15);\r\n      background-color: #7138cc;\r\n      text-align: center;\r\n    }\r\n\r\n    .sign-up-form {\r\n      display: flex;\r\n      flex-direction: column;\r\n      align-items: center;\r\n      padding: 1.2rem;\r\n      border-radius: 0.8rem;\r\n      box-shadow: 0 8px 0px rgba(0 0 0/0.15);\r\n      color: #b9b6d3;\r\n      background-color: white;\r\n    }\r\n\r\n    .form-input {\r\n      width: 100%;\r\n      margin-bottom: 1em;\r\n      position: relative;\r\n    }\r\n\r\n    .form-input span {\r\n      position: absolute;\r\n      top: 10%;\r\n      right: 0;\r\n      padding: 0 0.65em;\r\n      border-radius: 50%;\r\n      background-color: #ff7a7a;\r\n      color: white;\r\n      display: none;\r\n    }\r\n\r\n    .form-input.warning span {\r\n      display: inline-block;\r\n    }\r\n\r\n    .form-input input {\r\n      width: calc(100% - 20px);\r\n      padding: 10px;\r\n      border: 2px solid rgba(185, 182, 211, 0.25);\r\n      border-radius: 0.5em;\r\n      font-weight: 600;\r\n      color: #3e3c49;\r\n    }\r\n\r\n    .form-input input:focus {\r\n      outline: none;\r\n      border: 2px solid #b9b6d3;\r\n    }\r\n\r\n    .form-input.warning input {\r\n      border: 2px solid #ff7a7a;\r\n    }\r\n\r\n    .form-input p {\r\n      margin: 0.2em 0.75em 0 0;\r\n      display: none;\r\n      font-size: 0.75rem;\r\n      text-align: right;\r\n      font-style: italic;\r\n      color: #ff7a7a;\r\n    }\r\n\r\n    .form-input.warning p {\r\n      display: block;\r\n    }\r\n\r\n    .submit-btn {\r\n      cursor: pointer;\r\n      width: 100%;\r\n      padding: 1em;\r\n      margin-bottom: 1em;\r\n      border: none;\r\n      border-bottom: 5px solid #31bf81;\r\n      border-radius: 0.5em;\r\n      background-color: #38cc8c;\r\n      color: white;\r\n      font-weight: 600;\r\n      text-transform: uppercase;\r\n    }\r\n\r\n    .submit-btn:hover {\r\n      background-color: #5dd5a1;\r\n    }\r\n\r\n    .form-term {\r\n      margin-bottom: 0.75em;\r\n      font-size: 0.8rem;\r\n      text-align: center;\r\n    }\r\n\r\n    .form-term span {\r\n      font-weight: 700;\r\n      color: #ff7a7a;\r\n    }\r\n    \r\n    @media (min-width: 768px) {\r\n      body {\r\n        align-items: center;\r\n        min-height: 100vh;\r\n      }\r\n\r\n      main {\r\n        max-width: 100vw;\r\n        flex-direction: row;\r\n        justify-content: center;\r\n      }\r\n\r\n      .intro {\r\n        align-items: flex-start;\r\n        text-align: left;\r\n        width: 45%;\r\n        margin-right: 1rem;\r\n      }\r\n\r\n      .title {\r\n        padding: 0;\r\n        margin-bottom: 2rem;\r\n        font-size: 3rem;\r\n        line-height: 1.25em;\r\n      }\r\n\r\n      .sign-up {\r\n        width: 45%;\r\n      }\r\n\r\n      .sign-up-form {\r\n        padding: 1.75rem;\r\n      }\r\n\r\n      .sign-up-form input {\r\n        padding-left: 1.5em;\r\n      }\r\n    }\r\n  </style>\r\n</head>\r\n\r\n<body>\r\n  <main>\r\n    <!-- intro section -->\r\n    <section class=\"intro\">\r\n      <h1 class=\"title\">Black Friday Deals</h1>\r\n      <p>Get up to 50% off on all our products and services. Hurry up, the offer ends in 24 hours.</p>\r\n    </section>\r\n\r\n    <!-- sign-up section -->\r\n    <section class=\"sign-up\">\r\n      <p class=\"sign-up-para\">Sign up for our newsletter and get 10% off your first purchase</p>\r\n      <!-- the form itself -->\r\n      <form class=\"sign-up-form\">\r\n        <div class=\"form-input\">\r\n          <input type=\"text\" name=\"first-name\" id=\"first-name\" placeholder=\"First Name\" required>\r\n          <span>!</span>\r\n          <p class=\"warning\">First name cannot be empty</p>\r\n        </div>\r\n\r\n        <div class=\"form-input\">\r\n          <input type=\"text\" name=\"last-name\" id=\"last-name\" placeholder=\"Last Name\" required>\r\n          <span>!</span>\r\n          <p class=\"warning\">Last name cannot be empty</p>\r\n        </div>\r\n\r\n        <div class=\"form-input\">\r\n          <input type=\"email\" name=\"email\" id=\"email\" placeholder=\"Email Address\" required>\r\n          <span>!</span>\r\n          <p class=\"warning\">Looks like this is not an email</p>\r\n        </div>\r\n\r\n        <div class=\"form-input\">\r\n          <input type=\"Password\" name=\"Password\" id=\"Password\" placeholder=\"Password\" required>\r\n          <span>!</span>\r\n          <p class=\"warning\">Password cannot be empty</p>\r\n        </div>\r\n\r\n        <input class=\"submit-btn\" type=\"submit\" value=\"Claim your offer\">\r\n        <p class=\"form-term\">By clicking the button, you are